mapper.update:

update PBCRS_REPORT_ACTUCOTRLINFSGMT
set ACTUCOTRLNM = #{ActuCotrlNm,jdbcType=VARCHAR},
  ACTUCOTRLINFOUPDATE = #{ActuCotrlInfoUpDate,jdbcType=VARCHAR},
  ReportFlag=#{ReportFlag,jdbcType=VARCHAR}
where ACTUCOTRLINFSGMTSEQNO = #{ActuCotrlInfSgmtSeqNo,jdbcType=VARCHAR}

mapper.select:

select ACTUCOTRLINFSGMTSEQNO, ACTUCOTRLNM, ACTUCOTRLINFOUPDATE,ReportFlag
from PBCRS_REPORT_ACTUCOTRLINFSGMT
where ACTUCOTRLINFSGMTSEQNO = #{ActuCotrlInfSgmtSeqNo,jdbcType=VARCHAR}

    select count(1)
     from PBCRS_REPORT_EnBasInfActCotInf ids 
			    where ids.ActuCotrlInfSgmtSeqNo= #{ENBASINFSEQNO}


    select * from (
	    SELECT A.*, ROWNUM RN  from (
			    select ids.* 
     			from PBCRS_REPORT_EnBasInfActCotInf ids 
			     where ids.ActuCotrlInfSgmtSeqNo= #{ENBASINFSEQNO} 


		  	) A
	  	WHERE ROWNUM <=${endindex}
  	) where RN >=${startindex}

mapper.insert:

    insert into PBCRS_REPORT_ACTUCOTRLINFSGMTM (
    ACTUCOTRLINFSGMTSEQNO, ACTUCOTRLNM, ACTUCOTRLINFOUPDATE,ReportFlag,etl_date
		) select ACTUCOTRLINFSGMTSEQNO, ACTUCOTRLNM, ACTUCOTRLINFOUPDATE,'0',to_date(to_char(sysdate,'yyyy-mm-dd'),'yyyy-mm-dd')
		from PBCRS_REPORT_ACTUCOTRLINFSGMT
    where ACTUCOTRLINFSGMTSEQNO = #{ActuCotrlInfSgmtSeqNo,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_ACTUCOTRLINFSGMT
    set ReportFlag= #{flagRes}
