mapper.select:

select info.*
from PBCRS_REPORT_BalanceSheet2007 info
where info.BALANCESHEETBSSGMTSEQNO= #{BALANCESHEETBSSGMTSEQNO,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_BALANCESHEET2007
    set ReportFlag= #{flagRes}
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}
