mapper.select:

select info.*
from PBCRS_REPORT_CASHFLOWS2007SGMT info
where info.CASHFLOWS2007SGMTSEQNO = #{CashFlowsSeqNo,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_CASHFLOWS2007SGMT
    set ReportFlag= #{flagRes}
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}
