mapper.select:

    select * from (
	    SELECT A.*, ROWNUM RN  from (
			      select bb.*,bb.CASHFLOWSBSSGMTSEQNO CASHFLOWSSEQNO,mod.CASHFLOWSBSSGMTSEQNO AfterSeqno
              from  PBCRS_REPORT_CASHFLOWSBSSGMT bb
                    left join PBCRS_REPORT_CASHFLOWSBSSGMTm mod
              on bb.cashflowsbssgmtseqno=mod.cashflowsbssgmtseqno
          where 1=1 

		  	order by bb.rptdate desc,bb.cashflowsbssgmtseqno desc
		  	) A
	  	WHERE ROWNUM <=${endindex}
  	) where RN >${startindex}


select info.*
from PBCRS_REPORT_CASHFLOWSBSSGMT info
where info.CASHFLOWSBSSGMTSEQNO =  #{CashFlowsSeqNo,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_CASHFLOWSBSSGMT
    set ReportFlag= #{flagRes}
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}

update PBCRS_REPORT_CASHFLOWSBSSGMT
set RPTDATECODE= #{RptDateCode},
reportflag= #{reportFlag},
rptdate = #{rptDate}
where CASHFLOWSBSSGMTSEQNO = #{CashFlowsSeqNo}

update PBCRS_REPORT_CASHFLOWSBSSGMT
set reportflag= #{reportFlag},
rptdate = #{rptDate}
where CASHFLOWSBSSGMTSEQNO = #{CashFlowsSeqNo}

update PBCRS_REPORT_CASHFLOWSBSSGMT
