  )
 select CLTALINFSGMTSEQNO, IMPNM, '0'
from PBCRS_REPORT_CLTALINFSGMT
where CLTALINFSGMTSEQNO = #{CltalInfSgmtSeqNo,jdbcType=VARCHAR}

mapper.update:

update PBCRS_REPORT_CLTALINFSGMT
set IMPNM = #{ImpNm,jdbcType=VARCHAR},
  REPORTFLAG = #{reportflag,jdbcType=VARCHAR}
where CLTALINFSGMTSEQNO = #{CltalInfSgmtSeqNo,jdbcType=VARCHAR}

mapper.select:

select CLTALINFSGMTSEQNO, IMPNM, REPORTFLAG
from PBCRS_REPORT_CLTALINFSGMT
where CLTALINFSGMTSEQNO = #{CltalInfSgmtSeqNo,jdbcType=VARCHAR}

  select * from (
   	 select info.* ,rownum RN from
    	(
          select info1.CLTALINFSGMTSEQNO, IMPINFSEQNO, IMPTYPE, IMPVAL, IMPCY, IPPC, PAWNNAME, PAWNCERTTYPE, 
 			 PAWNCERTNUM
 			 from PBCRS_REPORT_CltalInfSgmt info1
       	join PBCRS_REPORT_ImpInf info2
       	on info1.cltalinfsgmtseqno=info2.cltalinfsgmtseqno
 			 where info1.CLTALINFSGMTSEQNO =  #{CltalInfSgmtSeqNo,jdbcType=VARCHAR}
    	 )info where rownum <#{endindex}
	) where RN >=#{startindex}

