mapper.delete:

delete from PBCRS_REPORT_COMRECINFSGMT
where COMRECINFSGMT
SEQNO = #{ComRecInfSgmtSeqNo,jdbcType=VARCHAR}

mapper.insert:

insert into PBCRS_REPORT_COMRECINFSGMT (COMRECINFSGMTSEQNO, GRTDNM, REPORTFLAG
  )
values (#{ComRecInfSgmtSeqNo,jdbcType=VARCHAR}, #{GrtdNm,jdbcType=VARCHAR}, #{reportflag,jdbcType=VARCHAR}
  )


insert into PBCRS_REPORT_COMRECINFSGMTM (COMRECINFSGMTSEQNO, GRTDNM, REPORTFLAG
  )
select COMRECINFSGMTSEQNO, GRTDNM, '0'
from PBCRS_REPORT_COMRECINFSGMT
where COMRECINFSGMTSEQNO = #{ComRecInfSgmtSeqNo,jdbcType=VARCHAR}

mapper.update:

update PBCRS_REPORT_COMRECINFSGMT
set GRTDNM = #{GrtdNm,jdbcType=VARCHAR},
  REPORTFLAG = #{reportflag,jdbcType=VARCHAR}
where COMRECINFSGMTSEQNO = #{ComRecInfSgmtSeqNo,jdbcType=VARCHAR}

mapper.select:

select COMRECINFSGMTSEQNO, GRTDNM, REPORTFLAG
from PBCRS_REPORT_COMRECINFSGMT
where COMRECINFSGMTSEQNO = #{ComRecInfSgmtSeqNo,jdbcType=VARCHAR}

  select * from (
   	 select info.* ,rownum RN from
    	(
            select info1.COMRECINFSGMTSEQNO, OTRRECSEQNO, INFOIDTYPE, GUARNAME, GUARCERTTYPE, GUARCERTNUM
        		from PBCRS_REPORT_ComRecInfSgmt info1
        		join  PBCRS_REPORT_OtrRec info2
       		 on info1.ComRecInfSgmtSeqNo=info2.ComRecInfSgmtSeqNo
        		where info1.ComRecInfSgmtSeqNo= #{ComRecInfSgmtSeqNo,jdbcType=VARCHAR} 
    	 )info where rownum <#{endindex}
	) where RN >=#{startindex}
