mapper.select:

select info.*
from PBCRS_REPORT_COTAINFSGMT info
where info.COTAINFSGMTSEQNO =  #{ENBASINFSEQNO,jdbcType=VARCHAR}


select COTAINFSGMTSEQNO, CONADDDISTRICTCODE, CONADD, CONPHONE, FINCONPHONE, COTAINFOUPDATE,reportFlag
from PBCRS_REPORT_COTAINFSGMT
where COTAINFSGMTSEQNO = #{CotaInfSgmtSeqNo,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_COTAINFSGMT
    set ReportFlag= #{flagRes}
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}

