mapper.select:
	select * from (
       select info1.*,ROWNUM RN from (
          select CtrctBsSgmtSeqNo,RptDate,Name,IDType,IDNum,MngmtOrgCode,ISDEL,SOURCESYS
           from  pbcrs_report_CtrctBsSgmt 
        	 where 1=1

               order by RptDate desc,CtrctBsSgmtSeqNo desc
       )info1 where  ROWNUM <#{endindex}
	)where RN >=#{startindex}

mapper.update:
update PBCRS_REPORT_CTRCTBSSGMT
set REPORTFLAG=#{flagRes}
where REPORTFLAG = '0'

	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}
