mapper.insert:

insert into PBCRS_REPORT_CTRCTCERTRELSGMT (CTRCTCERTRELSGMTSEQNO, BRERNM, REPORTFLAG
  )
values (#{CtrctCertRelSgmtSeqNo,jdbcType=VARCHAR}, #{BrerNm,jdbcType=VARCHAR}, #{reportflag,jdbcType=VARCHAR}
  )

mapper.update:

update PBCRS_REPORT_CTRCTCERTRELSGMT
set BRERNM = #{BrerNm,jdbcType=VARCHAR},
  REPORTFLAG = #{reportflag,jdbcType=VARCHAR}
where CTRCTCERTRELSGMTSEQNO = #{CtrctCertRelSgmtSeqNo,jdbcType=VARCHAR}

mapper.select:

select CTRCTCERTRELSGMTSEQNO, BRERNM, REPORTFLAG
from PBCRS_REPORT_CTRCTCERTRELSGMT
where CTRCTCERTRELSGMTSEQNO = #{CtrctCertRelSgmtSeqNo,jdbcType=VARCHAR}

  select * from (
       select info.*,rownum rn from (
              select info2.CtrctCertRelSgmtSeqNo,info2.CtrctCertRelSeqNo,info2.BRERTYPE,info2.CERTRELNAME,info2.CERTRELIDTYPE,info2.CERTRELIDNUM
 				from  pbcrs_report_CtrctCertRelSgmt info1
				 join Pbcrs_Report_CtrctCertRel info2
				on info1.ctrctcertrelsgmtseqno=info2.ctrctcertrelsgmtseqno
				where info1.ctrctcertrelsgmtseqno=#{CtrctCertRelSgmtSeqNo}

              ) info
       where rownum < #{endindex}
   )where rn >= #{startindex}
