mapper.update:

update PBCRS_REPORT_ENCTRCTINFIDCAGSI
set INFRECTYPE = #{InfRecType,jdbcType=VARCHAR},
ODBNESCODE = #{OdBnesCode,jdbcType=VARCHAR},
NWBNESCODE = #{NwBnesCode,jdbcType=VARCHAR}
where ENCTRCTINFIDCAGSINFSEQNO = #{EnCtrctInfIDCagsInfSeqNo,jdbcType=VARCHAR}

mapper.select:

select ENCTRCTINFIDCAGSINFSEQNO, INFRECTYPE, ODBNESCODE, NWBNESCODE
from PBCRS_REPORT_ENCTRCTINFIDCAGSI
where ENCTRCTINFIDCAGSINFSEQNO = #{EnCtrctInfIDCagsInfSeqNo,jdbcType=VARCHAR}

select ENCTRCTINFIDCAGSINFSEQNO, INFRECTYPE, ODBNESCODE, NWBNESCODE
from PBCRS_REPORT_ENCTRCTINFIDCAGSI
where ETL_DATE = #{etl_Date}

	and SOURCESYS = #{sysid}

and REPORTFLAG = #{REPORTFLAG}
