mapper.select:
    select ACCTBSINFSGMTSEQNO, BUSILINES, BUSIDTLLINES, OPENDATE, CY, ACCTCREDLINE, LOANAMT, 
    FLAG, DUEDATE, REPAYMODE, REPAYFREQCY, APPLYBUSIDIST, GUARMODE, OTHREPYGUARWAY, LOANTIMELIMCAT, 
    LOAFRM, ACTINVEST, FUNDSOU, ASSETTRANDFLAG,ReportFlag
    from PBCRS_REPORT_ENACCTBSINFSGMTm
    where ACCTBSINFSGMTSEQNO = #{MdfcSgmtDetailSeqNo,jdbcType=VARCHAR}

		and REPORTFLAG = #{REPORTFLAG}


select info.*
from PBCRS_REPORT_ENACCTBSINFSGMT info
where info.AcctBsInfSgmtSeqNo = #{AcctBsInfSgmtSeqNo,jdbcType=VARCHAR}


select info.*
from PBCRS_REPORT_ENACCTBSINFSGMTM info
where info.AcctBsInfSgmtSeqNo = #{AcctBsInfSgmtSeqNo,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_ENACCTBSINFSGMT
    set ReportFlag= #{flagRes}
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}

    update PBCRS_REPORT_ENACCTBSINFSGMTm
    set ReportFlag= '1'
    where ETL_DATE = #{etl_Date}

			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}
