mapper.select:

    select ACCTBSSGMTSEQNO, NAME, 
    IDTYPE, IDNUM, MNGMTORGCODE
    from pbcrs_report_EnAcctBsSgmtm
    where ACCTBSSGMTSEQNO = #{MdfcSgmtDetailSeqNo,jdbcType=VARCHAR}

		and REPORTFLAG = #{REPORTFLAG}


    select * from (
	    SELECT A.*, ROWNUM RN  from (
			      select bb.*,mod.AcctBsSgmtSeqNo AfterSeqno
		          from PBCRS_REPORT_ENACCTBSSGMT bb
		          left join PBCRS_REPORT_ENACCTBSSGMTM mod 
		          on bb.AcctBsSgmtSeqNo=mod.AcctBsSgmtSeqNo
			    where 1=1 

		 order by  bb.rptdate desc,nvl(bb.bad_flag,'Z') asc,bb.AcctBsSgmtSeqNo desc
		  	) A
	  	WHERE ROWNUM <=${endindex}
  	) where RN >${startindex}


select info.*
from PBCRS_REPORT_ENACCTBSSGMT info
where info.AcctBsSgmtSeqNo = #{AcctBsSgmtSeqNo,jdbcType=VARCHAR}


select info.*
from PBCRS_REPORT_ENACCTBSSGMTM info
where info.AcctBsSgmtSeqNo = #{AcctBsSgmtSeqNo,jdbcType=VARCHAR}

mapper.insert:

    insert into pbcrs_report_EnAcctBsSgmtm (ACCTBSSGMTSEQNO, INFRECTYPE, ACCTTYPE, ACCTCODE, RPTDATE, RPTDATECODE, NAME, 
    IDTYPE, IDNUM, MNGMTORGCODE,ReportFlag,etl_date
		) select ACCTBSSGMTSEQNO, INFRECTYPE, ACCTTYPE, ACCTCODE, to_char(sysdate,'yyyy-MM-dd'), RPTDATECODE, NAME, 
    IDTYPE, IDNUM, MNGMTORGCODE,'0',to_date(to_char(sysdate,'yyyy-mm-dd'),'yyyy-mm-dd')
		 from pbcrs_report_EnAcctBsSgmt  where AcctBsSgmtSeqNo = #{AcctBsSgmtSeqNo,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_ENACCTBSSGMT
    set ReportFlag= #{flagRes}
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}

    update pbcrs_report_EnAcctBsSgmtm
    set ReportFlag= '1'
    where ETL_DATE = #{etl_Date}

			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}

update PBCRS_REPORT_ENACCTBSSGMT
set IsDel= #{IsDel}
where AcctBsSgmtSeqNo = #{AcctBsSgmtSeqNo}

update PBCRS_REPORT_ENACCTBSSGMT t
set t.bad_flag = 'Y'
where t.acctbssgmtseqno = #{seqNo}
