mapper.delete:

delete from PBCRS_REPORT_ENACCTINFDEL
where ENACCTINFIDCAGSINFSEQNO = #{EnAcctInfIDCagsInfSeqNo,jdbcType=VARCHAR}
and DELSGMTCODE = #{DelSgmtCode,jdbcType=VARCHAR}

mapper.select:

select ENACCTINFIDCAGSINFSEQNO, INFRECTYPE, DELRECCODE, DELSGMTCODE,
DELSTARTDATE,
DELENDDATE
from PBCRS_REPORT_ENACCTINFDEL
where 1=1

	and ETL_DATE = #{etl_Date}


	and SOURCESYS = #{sysid}

and REPORTFLAG = #{REPORTFLAG}
