mapper.delete:

delete from PBCRS_REPORT_ENBASINFACTCOTINF
where ACTUCOTRLINFSGMTSEQNO = #{ActuCotrlInfSgmtSeqNo,jdbcType=VARCHAR}
  and ACTUCOTRLINFSEQNO = #{ActuCotrlInfSeqNo,jdbcType=VARCHAR}

mapper.update:

update PBCRS_REPORT_ENBASINFACTCOTINF
set ACTUCOTRLCERTTYPE = #{ActuCotrlCertType,jdbcType=VARCHAR},
  ACTUCOTRLNAME = #{ActuCotrlName,jdbcType=VARCHAR},
  ACTUCOTRLIDTYPE = #{ActuCotrlIDType,jdbcType=VARCHAR},
  ACTUCOTRLIDNUM = #{ActuCotrlIDNum,jdbcType=VARCHAR},ReportFlag=#{ReportFlag,jdbcType=VARCHAR}
where ACTUCOTRLINFSGMTSEQNO = #{ActuCotrlInfSgmtSeqNo,jdbcType=VARCHAR}
  and ACTUCOTRLINFSEQNO = #{ActuCotrlInfSeqNo,jdbcType=VARCHAR}

mapper.select:

select ACTUCOTRLINFSGMTSEQNO, ACTUCOTRLINFSEQNO, ACTUCOTRLCERTTYPE, ACTUCOTRLNAME, 
ACTUCOTRLIDTYPE, ACTUCOTRLIDNUM,ReportFlag
from PBCRS_REPORT_ENBASINFACTCOTINF
where ACTUCOTRLINFSGMTSEQNO = #{ActuCotrlInfSgmtSeqNo,jdbcType=VARCHAR}
  and ACTUCOTRLINFSEQNO = #{ActuCotrlInfSeqNo,jdbcType=VARCHAR}

