mapper.insert:

insert into PBCRS_REPORT_ENBASINFBSSGMT (BSSGMTSEQNO, INFRECTYPE, ENTNAME, 
  ENTCERTTYPE, ENTCERTNUM, INFSURCCODE, 
  RPTDATE, RPTDATECODE, CIMOC, 
  CUSTOMERTYPE, ETPSTS, ORGTYPE,ReportFlag
  )
values (#{BsSgmtSeqNo,jdbcType=VARCHAR}, #{InfRecType,jdbcType=VARCHAR}, #{EntName,jdbcType=VARCHAR}, 
  #{EntCertType,jdbcType=VARCHAR}, #{EntCertNum,jdbcType=VARCHAR}, #{InfSurcCode,jdbcType=VARCHAR}, 
  #{RptDate,jdbcType=VARCHAR}, #{RptDateCode,jdbcType=VARCHAR}, #{Cimoc,jdbcType=VARCHAR}, 
  #{CustomerType,jdbcType=VARCHAR}, #{EtpSts,jdbcType=VARCHAR}, #{OrgType,jdbcType=VARCHAR}, #{ReportFlag,jdbcType=VARCHAR}
  )

mapper.select:

    select * from (
	    SELECT A.*, ROWNUM RN  from (
			      select bb.*,eb.bssgmtseqno AfterSeqno
		          from PBCRS_REPORT_ENBASINFBSSGMT bb
		          left join PBCRS_REPORT_ENBASINFBSSGMTM eb 
		          on bb.bssgmtseqno=eb.bssgmtseqno

			    where 1=1 

		  	order by bb.rptdate desc ,bb.bssgmtseqno desc
		  	) A
	  	WHERE ROWNUM <=${endindex}
  	) where RN >${startindex}


select info.*
from PBCRS_REPORT_ENBASINFBSSGMT info
where info.bssgmtseqno = #{ENBASINFSEQNO,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_ENBASINFBSSGMT
    set ReportFlag= #{flagRes}
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}

update PBCRS_REPORT_ENBASINFBSSGMT
set RPTDATECODE= #{RptDateCode},
reportflag= #{reportFlag},
rptdate = #{rptDate}
where BsSgmtSeqNo = #{BsSgmtSeqNo}

update PBCRS_REPORT_ENBASINFBSSGMT
set reportflag= #{reportFlag},
rptdate = #{rptDate}
where BsSgmtSeqNo = #{BsSgmtSeqNo}

update PBCRS_REPORT_ENBASINFBSSGMT
set IsDel= #{IsDel}
where BsSgmtSeqNo = #{BsSgmtSeqNo}
