mapper.delete:

delete from PBCRS_REPORT_ENBASINFIDREC
where IDSGMTSEQNO = #{IDSgmtSeqNo,jdbcType=VARCHAR}
  and IDSGMTLISTSEQNO = #{IDSgmtListSeqNo,jdbcType=VARCHAR}

mapper.update:

update PBCRS_REPORT_ENBASINFIDREC
set OTHENTCERTTYPE = #{OthEntCertType,jdbcType=VARCHAR},
  OTHENTCERTNUM = #{OthEntCertNum,jdbcType=VARCHAR},
  ReportFlag=#{ReportFlag,jdbcType=VARCHAR}
where IDSGMTSEQNO = #{IDSgmtSeqNo,jdbcType=VARCHAR}
  and IDSGMTLISTSEQNO = #{IDSgmtListSeqNo,jdbcType=VARCHAR}

mapper.select:

select IDSGMTSEQNO, IDSGMTLISTSEQNO, OTHENTCERTTYPE, OTHENTCERTNUM,ReportFlag
from PBCRS_REPORT_ENBASINFIDREC
where IDSGMTSEQNO = #{IDSgmtSeqNo,jdbcType=VARCHAR}
  and IDSGMTLISTSEQNO = #{IDSgmtListSeqNo,jdbcType=VARCHAR}


select IDSGMTSEQNO, IDSGMTLISTSEQNO, OTHENTCERTTYPE, OTHENTCERTNUM,ReportFlag
from PBCRS_REPORT_ENBASINFIDREC
where IDSGMTSEQNO=#{IDSgmtSeqNo,jdbcType=VARCHAR}

mapper.insert:

    insert into PBCRS_REPORT_ENBASINFIDRECM (IDSGMTSEQNO, IDSGMTLISTSEQNO, OTHENTCERTTYPE, OTHENTCERTNUM,ReportFlag,etl_date

		) select IDSGMTSEQNO, IDSGMTLISTSEQNO, OTHENTCERTTYPE, OTHENTCERTNUM,'0',to_date(to_char(sysdate,'yyyy-mm-dd'),'yyyy-mm-dd')
		 from PBCRS_REPORT_ENBASINFIDREC
    	where IDSGMTSEQNO=#{IDSgmtSeqNo,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_ENBASINFIDREC
    set ReportFlag= #{flagRes}
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}

