mapper.insert:

insert into PBCRS_REPORT_ENBSINFDLT (ENBSINFDLTSEQNO, INFRECTYPE,
INFSURCCODE,
ENTNAME, ENTCERTTYPE, ENTCERTNUM,ETL_DATE,SOURCESYS,REPORTFLAG,ORG_CODE
)
values (#{EnBsInfDltSeqNo,jdbcType=VARCHAR}, #{InfRecType,jdbcType=VARCHAR},
#{InfSurcCode,jdbcType=VARCHAR},
#{EntName,jdbcType=VARCHAR}, #{EntCertType,jdbcType=VARCHAR}, #{EntCertNum,jdbcType=VARCHAR},to_date(to_char(sysdate,'yyyy-mm-dd'),'yyyy-mm-dd'),
#{SourceSys,jdbcType=VARCHAR},#{ReportFlag,jdbcType=VARCHAR},#{orgCode,jdbcType=VARCHAR}
)

mapper.select:
select ENBSINFDLTSEQNO, INFRECTYPE, INFSURCCODE, ENTNAME, ENTCERTTYPE,
ENTCERTNUM
from PBCRS_REPORT_ENBSINFDLT
where ETL_DATE = #{etl_Date}

	and SOURCESYS = #{sysid}

and REPORTFLAG = #{REPORTFLAG}
