  TRANDATE = #{TranDate,jdbcType=VARCHAR},
  TRANAMT = #{TranAmt,jdbcType=VARCHAR},
  DUETRANMON = #{DueTranMon,jdbcType=VARCHAR},
  DETINFO = #{DetInfo,jdbcType=VARCHAR},
  ReportFlag=#{ReportFlag,jdbcType=VARCHAR},
  PARAGRAPHDEL = #{ParagraphDel,jdbcType=VARCHAR}
where ACCTSPECTRSTDSPNSGMTSEQNO = #{AcctSpecTrstDspnSgmtSeqNo,jdbcType=VARCHAR}
  and CAGOFTRDINFSEQNO = #{CagOfTrdInfSeqNo,jdbcType=VARCHAR}

mapper.select:

select ACCTSPECTRSTDSPNSGMTSEQNO, CAGOFTRDINFSEQNO, CHANTRANTYPE, TRANDATE, TRANAMT, 
DUETRANMON, DETINFO,ReportFlag,PARAGRAPHDEL
from PBCRS_REPORT_ENCAGOFTRDINF
where ACCTSPECTRSTDSPNSGMTSEQNO = #{AcctSpecTrstDspnSgmtSeqNo,jdbcType=VARCHAR}
  and CAGOFTRDINFSEQNO = #{CagOfTrdInfSeqNo,jdbcType=VARCHAR}

    select ACCTSPECTRSTDSPNSGMTSEQNO, CAGOFTRDINFSEQNO, CHANTRANTYPE, TRANDATE, TRANAMT, 
    DUETRANMON, DETINFO,ReportFlag
    from PBCRS_REPORT_ENCAGOFTRDINFm
