		and REPORTFLAG = #{REPORTFLAG}

mapper.insert:

    insert into PBCRS_REPORT_ENCAGOFTRDINFm (ACCTSPECTRSTDSPNSGMTSEQNO, CAGOFTRDINFSEQNO, CHANTRANTYPE, TRANDATE, TRANAMT, 
