		) select ACCTSPECTRSTDSPNSGMTSEQNO, CAGOFTRDINFSEQNO, CHANTRANTYPE, TRANDATE, TRANAMT, 
    DUETRANMON, DETINFO,'0',to_date(to_char(sysdate,'yyyy-mm-dd'),'yyyy-mm-dd')
		 from PBCRS_REPORT_ENCAGOFTRDINF  where ACCTSPECTRSTDSPNSGMTSEQNO = #{AcctSpecTrstDspnSgmtSeqNo,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_ENCAGOFTRDINF
    set ReportFlag= #{flagRes}
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}


			and ACCTSPECTRSTDSPNSGMTSEQNO in(${seqNoS})

		and REPORTFLAG = #{REPORTFLAG}

    update PBCRS_REPORT_ENCAGOFTRDINFm
