mapper.select:

    select CREDITLIMSGMTSEQNO, CREDITLIMTYPE, LIMLOOPFLG, CREDITLIM, CY, CONEFFDATE, 
    CONEXPDATE, CONSTATUS, CREDITRESTCODE, CREDITREST,REPORTFLAG
    from PBCRS_REPORT_ENCREDITLIMSGMTM
    where CREDITLIMSGMTSEQNO = #{MdfcSgmtDetialSeqNo,jdbcType=VARCHAR}

		and REPORTFLAG = #{REPORTFLAG}

mapper.update:
    update PBCRS_REPORT_ENCREDITLIMSGMT
    set ReportFlag= #{flagRes}
    where ETL_DATE = #{etl_Date}

			and SOURCESYS = #{sysid}


			and CREDITLIMSGMTSEQNO in(${seqNoS})

		and REPORTFLAG = #{REPORTFLAG}

    update PBCRS_REPORT_ENCREDITLIMSGMTM
    set ReportFlag= '1'
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}


			and CREDITLIMSGMTSEQNO in(${seqNoS})

		and REPORTFLAG = #{REPORTFLAG}
