mapper.insert:

insert into PBCRS_REPORT_ENCTRCTBSSGMT (CTRCTBSSGMTSEQNO, INFRECTYPE, CONTRACTCODE, 
  RPTDATE, RPTDATECODE, NAME, 
  IDTYPE, IDNUM, MNGMTORGCODE,REPORTFLAG
  )
values (#{CtrctBsSgmtSeqNo,jdbcType=VARCHAR}, #{InfRecType,jdbcType=VARCHAR}, #{ContractCode,jdbcType=VARCHAR}, 
  #{RptDate,jdbcType=VARCHAR}, #{RptDateCode,jdbcType=VARCHAR}, #{Name,jdbcType=VARCHAR}, 
  #{IDType,jdbcType=VARCHAR}, #{IDNum,jdbcType=VARCHAR}, #{MngmtOrgCode,jdbcType=VARCHAR},#{ReportFlag,jdbcType=VARCHAR}
  )

mapper.select:

    select CTRCTBSSGMTSEQNO, NAME, IDTYPE, 
    IDNUM, MNGMTORGCODE
    from PBCRS_REPORT_ENCTRCTBSSGMTM
    where CTRCTBSSGMTSEQNO = #{MdfcSgmtDetialSeqNo,jdbcType=VARCHAR}
     and    ETL_DATE = #{etl_Date}

			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}


    select * from (
	    SELECT A.*, ROWNUM RN  from (
	     			select eb.*,eb.CtrctBsSgmtSeqNo ENCTRCTINFSEQNO,mod.CtrctBsSgmtSeqNo AFTERSEQNO
			       from   PBCRS_REPORT_EnCtrctBsSgmt eb 
			        left join PBCRS_REPORT_EnCtrctBsSgmtm mod
             on eb.ctrctbssgmtseqno=mod.ctrctbssgmtseqno
			    where 1=1 

		  	order by eb.rptdate desc,eb.CtrctBsSgmtSeqNo desc
		  	) A
	  	WHERE ROWNUM <=${endindex}
  	) where RN >${startindex}


select info.*
from PBCRS_REPORT_ENCTRCTBSSGMT info
where info.CtrctBsSgmtSeqNo =  #{EnCtrctInfSeqNo,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_ENCTRCTBSSGMT
    set ReportFlag= #{flagRes}
    where ETL_DATE = #{etl_Date}

			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}

    update PBCRS_REPORT_ENCTRCTBSSGMTM
    set ReportFlag= '1'
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}

update PBCRS_REPORT_ENCTRCTBSSGMT
set IsDel= #{IsDel}
where CTRCTBSSGMTSEQNO = #{EnCtrctInfSeqNo}
