mapper.delete:

delete from PBCRS_REPORT_ENCTRCTCERTREL
where CTRCTCERTRELSGMTSEQNO = #{CtrctCertRelSgmtSeqNo,jdbcType=VARCHAR}
  and CTRCTCERTRELSEQNO = #{CtrctCertRelSeqNo,jdbcType=VARCHAR}

mapper.update:

update PBCRS_REPORT_ENCTRCTCERTREL
set BRERTYPE = #{BrerType,jdbcType=VARCHAR},
  CERTRELNAME = #{CertRelName,jdbcType=VARCHAR},
  CERTRELIDTYPE = #{CertRelIDType,jdbcType=VARCHAR},
  CERTRELIDNUM = #{CertRelIDNum,jdbcType=VARCHAR},
  REPORTFLAG = #{ReportFlag,jdbcType=VARCHAR}
where CTRCTCERTRELSGMTSEQNO = #{CtrctCertRelSgmtSeqNo,jdbcType=VARCHAR}
  and CTRCTCERTRELSEQNO = #{CtrctCertRelSeqNo,jdbcType=VARCHAR}

mapper.select:

select CTRCTCERTRELSGMTSEQNO, CTRCTCERTRELSEQNO, BRERTYPE, CERTRELNAME, CERTRELIDTYPE, 
CERTRELIDNUM,REPORTFLAG
from PBCRS_REPORT_ENCTRCTCERTREL
where CTRCTCERTRELSGMTSEQNO = #{CtrctCertRelSgmtSeqNo,jdbcType=VARCHAR}
  and CTRCTCERTRELSEQNO = #{CtrctCertRelSeqNo,jdbcType=VARCHAR}

