mapper.insert:

    insert into PBCRS_REPORT_ENORICREINFSGMTM (ORIGCREDITORINFSGMTSEQNO, INITCREDNAME, INITCEDORGCODE, ORIGDBTCATE, INITRPYSTS,ReportFlag,etl_date
		) select ORIGCREDITORINFSGMTSEQNO, INITCREDNAME, INITCEDORGCODE, ORIGDBTCATE, INITRPYSTS,'0',to_date(to_char(sysdate,'yyyy-mm-dd'),'yyyy-mm-dd')
		 from PBCRS_REPORT_ENORICREINFSGMT  where ORIGCREDITORINFSGMTSEQNO = #{ORIGCREDITORINFSGMTSEQNO,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_ENORICREINFSGMT
    set ReportFlag= #{flagRes}
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}

    update PBCRS_REPORT_ENORICREINFSGMTM
    set ReportFlag= '1'
    where ETL_DATE = #{etl_Date}

			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}
