mapper.delete:

delete from PBCRS_REPORT_ENSECACCTDEL
where ENSECACCTDELSEQNO =
#{EnSecAcctDelSeqNo,jdbcType=VARCHAR}
and DELSGMTCODE=#{DelSgmtCode,jdbcType=VARCHAR}

mapper.select:

select ENSECACCTDELSEQNO, DELRECTYPE, DELRECCODE, DELSGMTCODE,
DELSTARTDATE, DELENDDATE
from PBCRS_REPORT_ENSECACCTDEL
where ETL_DATE =
#{etl_Date}

	and SOURCESYS = #{sysid}

and REPORTFLAG = #{REPORTFLAG}
