mapper.delete:

delete from PBCRS_REPORT_GUACCCINF
where GUAMOTCLTCTRINFSGMTSEQNO = #{GuaMotCltCtrInfSgmtSeqNo,jdbcType=VARCHAR}
  and CCCINFSEQNO = #{CccInfSeqNo,jdbcType=VARCHAR}

mapper.update:

update PBCRS_REPORT_GUACCCINF
set CCC = #{Ccc,jdbcType=VARCHAR}
where GUAMOTCLTCTRINFSGMTSEQNO = #{GuaMotCltCtrInfSgmtSeqNo,jdbcType=VARCHAR}
  and CCCINFSEQNO = #{CccInfSeqNo,jdbcType=VARCHAR}

mapper.select:

select GUAMOTCLTCTRINFSGMTSEQNO, CCCINFSEQNO, CCC
from PBCRS_REPORT_GUACCCINF
where GUAMOTCLTCTRINFSGMTSEQNO = #{GuaMotCltCtrInfSgmtSeqNo,jdbcType=VARCHAR}
  and CCCINFSEQNO = #{CccInfSeqNo,jdbcType=VARCHAR}

