mapper.select:
    select GUARRLTREPYMTINFSGMTSEQNO, ACCTSTATUS, LOANAMT, REPAYPRD, FIVECATE, FIVECATEADJDATE, 
    RIEX, COMPADVFLAG, CLOSEDATE, REPORTFLAG
    from PBCRS_REPORT_GUARLTREPINFSGMTM
    where GUARRLTREPYMTINFSGMTSEQNO = #{MdfcSgmtDetialSeqNo,jdbcType=VARCHAR}

		and REPORTFLAG = #{REPORTFLAG}

mapper.update:
    update PBCRS_REPORT_GUARLTREPINFSGMT
    set ReportFlag= '1'
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}

    update PBCRS_REPORT_GUARLTREPINFSGMTM
    set ReportFlag= '1'
    where ETL_DATE = #{etl_Date}

			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}
