mapper.delete:

delete from PBCRS_REPORT_INACCTDEL
where INACCTDELSEQNO =
#{InAcctDelSeqNo,jdbcType=VARCHAR}
and DELSGMTCODE = #{DelSgmtCode,jdbcType=VARCHAR}

mapper.select:
select INACCTDELSEQNO, INFRECTYPE, DELRECCODE, DELSGMTCODE,
nvl(DELSTARTDATE,' ') DELSTARTDATE, nvl(DELENDDATE,' ') DELENDDATE
from PBCRS_REPORT_INACCTDEL where
reportflag='0'

	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}



	and etl_date=#{etl_Date}

mapper.update:
update PBCRS_REPORT_INACCTDEL
set REPORTFLAG='1'
where REPORTFLAG = '0'

	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}
