mapper.select:
select INACCTIDCAGSINFSEQNO, INFRECTYPE, ODBNESCODE, NWBNESCODE,
REPORTFLAG
from PBCRS_REPORT_INACCTIDCAGSINF
where reportflag='0'

	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}

mapper.update:
update PBCRS_REPORT_INACCTIDCAGSINF
set REPORTFLAG='1'
where REPORTFLAG
= '0'

	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}
