mapper.insert:

insert into PBCRS_REPORT_INBSINFDLT (INBSINFDLTSEQNO, INFRECTYPE,
NAME,
IDTYPE, IDNUM, INFSURCCODE,REPORTFLAG,ETL_DATE,SOURCESYS,ORG_CODE
)
values (#{InBsInfDltSeqNo,jdbcType=VARCHAR}, #{InfRecType,jdbcType=VARCHAR},
#{Name,jdbcType=VARCHAR},
#{IDType,jdbcType=VARCHAR}, #{IDNum,jdbcType=VARCHAR}, #{InfSurcCode,jdbcType=VARCHAR}, #{ReportFlag,jdbcType=VARCHAR}
, #{EtlDate,jdbcType=DATE}, #{SourceSys,jdbcType=VARCHAR},#{orgCode,jdbcType=VARCHAR}
)

mapper.select:

select INBSINFDLTSEQNO, INFRECTYPE, NAME, IDTYPE, IDNUM, INFSURCCODE,REPORTFLAG
from PBCRS_REPORT_INBSINFDLT
where INBSINFDLTSEQNO = #{InBsInfDltSeqNo,jdbcType=VARCHAR}

select INBSINFDLTSEQNO, INFRECTYPE, NAME, IDTYPE, IDNUM, INFSURCCODE,REPORTFLAG
from PBCRS_REPORT_INBSINFDLT
where reportflag='0'

	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}
