mapper.select:

    select * from (
	    SELECT A.*, ROWNUM RN  from (
			      select bb.*,bb.INCOMEANDESBSSGMTSEQNO INCOMEANDESSEQNO,mod.INCOMEANDESBSSGMTSEQNO AfterSeqno
              from PBCRS_REPORT_INCANDEXPSTBSSGMT bb
                      left join PBCRS_REPORT_INCANDEXPSTBSSGMM mod
              on bb.incomeandesbssgmtseqno=mod.incomeandesbssgmtseqno
          where 1=1 

		  	order by bb.rptdate desc,bb.incomeandesbssgmtseqno desc
		  	) A
	  	WHERE ROWNUM <=${endindex}
  	) where RN >${startindex}


select info.*
from PBCRS_REPORT_INCANDEXPSTBSSGMT info 
where  info.INCOMEANDESBSSGMTSEQNO = #{IncomeAndESSeqNo,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_INCANDEXPSTBSSGMT
    set ReportFlag= #{flagRes}
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}

update PBCRS_REPORT_INCANDEXPSTBSSGMT
set RPTDATECODE= #{RptDateCode},
reportflag= #{reportFlag},
rptdate = #{rptDate}
where INCOMEANDESBSSGMTSEQNO = #{IncomeAndESBsSgmtSeqNo}

update PBCRS_REPORT_INCANDEXPSTBSSGMT
set reportflag= #{reportFlag},
rptdate = #{rptDate}
where INCOMEANDESBSSGMTSEQNO = #{IncomeAndESBsSgmtSeqNo}

update PBCRS_REPORT_INCANDEXPSTBSSGMT
set IsDel= #{IsDel}
where INCOMEANDESBSSGMTSEQNO = #{IncomeAndESSeqNo}
