mapper.select:

    select * from (
	    SELECT A.*, ROWNUM RN  from (
			      select bb.*,bb.INCOMESTATEMENTPABSSGMTSEQNO INCOMESTATEMENTPASEQNO,mod.INCOMESTATEMENTPABSSGMTSEQNO AfterSeqno
              from pbcrs_report_IncomeSBsSgmt bb
               left join pbcrs_report_IncomeSBsSgmtm mod 
              on bb.incomestatementpabssgmtseqno = mod.incomestatementpabssgmtseqno
          where 1=1 


		  	order by bb.rptdate desc,bb.INCOMESTATEMENTPABSSGMTSEQNO desc
		  	) A
	  	WHERE ROWNUM <=${endindex}
  	) where RN >${startindex}


select info.*
from pbcrs_report_IncomeSBsSgmt info 
where info.INCOMESTATEMENTPABSSGMTSEQNO= #{INCOMESTATEMENTPABSSGMTSEQNO,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_INCOMESBSSGMTM
    set ReportFlag= #{flagRes}
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}

update PBCRS_REPORT_INCOMESBSSGMT
set RPTDATECODE= #{RptDateCode},
reportflag= #{reportFlag},
rptdate = #{rptDate}
where INCOMESTATEMENTPABSSGMTSEQNO = #{IncomeStatementPASeqNo}

update pbcrs_report_IncomeSBsSgmt
set IsDel= #{IsDel}
where INCOMESTATEMENTPABSSGMTSEQNO = #{IncomeStatementPASeqNo}
