mapper.select:
select INCTFITGINFSEQNO, INFRECTYPE, NAME, IDTYPE, IDNUM, OTHNAME, OTHIDTYPE, OTHASSFLG, INFSURCCODE, RPTDATE, OTHIDNUM from PBCRS_REPORT_INCTFITGINF where reportflag='0'
and sourcesys=#{sysid}
and etl_date=#{etl_Date}

mapper.update:
update PBCRS_REPORT_INCTFITGINF
set REPORTFLAG='1'
where REPORTFLAG =
'0'

	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}
