DELRECCODE,
DELSGMTCODE, DELSTARTDATE, DELENDDATE
)
values (#{InCtrctDelSeqNo,jdbcType=VARCHAR}, #{InfRecType,jdbcType=VARCHAR},
#{DelRecCode,jdbcType=VARCHAR},
#{DelSgmtCode,jdbcType=VARCHAR}, #{DelStartDate,jdbcType=VARCHAR}, #{DelEndDate,jdbcType=VARCHAR}
)

mapper.select:
select INCTRCTDELSEQNO, INFRECTYPE, DELRECCODE, DELSGMTCODE,
DELSTARTDATE, DELENDDATE
from PBCRS_REPORT_INCTRCTDEL

where reportflag='0'

	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}

mapper.update:
update PBCRS_REPORT_INCTRCTDEL
set REPORTFLAG='1'
where REPORTFLAG = '0'

	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}

