mapper.select:
		select * from (
			select t.*,rownum rm from (
				select INFALMMBSINFSEQNO, INFRECTYPE, NAME, IDTYPE, IDNUM, FAMMEMNAME,
						FAMMEMCERTTYPE,
						FAMMEMCERTNUM, FAMREL, FAMRELAASSFLAG, INFSURCCODE,
						RPTDATE
				from PBCRS_REPORT_INFALMMBSINF

				where reportflag='0'

				and sourcesys=#{sysid}



				and etl_date=#{etl_Date}

			)t where rownum <= #{pageEnd}
  		 )where rm > #{pageBegin}

select count(1)
from PBCRS_REPORT_INFALMMBSINF

where reportflag='0'

	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}

mapper.update:
update PBCRS_REPORT_INFALMMBSINF
set REPORTFLAG=#{flagRes}
where REPORTFLAG =
'0'

	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}
