mapper.select:
select * from ( select t.*,rownum rm from ( select INIDEFCTINFDLTSEQNO, INFRECTYPE, NAME, IDTYPE, IDNUM, INFSURCCODE from PBCRS_REPORT_INIDEFCTINF where reportflag='0'
and sourcesys=#{sysid}
and etl_date=#{etl_Date}
)t where rownum <= #{pageEnd} ) where rm > #{pageBegin}

select count(1)
from PBCRS_REPORT_INIDEFCTINF

where reportflag='0'

	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}

mapper.update:
update PBCRS_REPORT_INIDEFCTINFDLT
set REPORTFLAG='1'
where REPORTFLAG =
'0'

	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}
