mapper.select:

    select * from (
	    SELECT A.*, ROWNUM RN  from (
			      select bb.*,bb.INSTITUTIONBSBSSGMTSEQNO INSTITUTIONBSSEQNO,mod.INSTITUTIONBSBSSGMTSEQNO AfterSeqno
              from PBCRS_REPORT_INSBALSHEBSSGMT bb
             left join PBCRS_REPORT_INSBALSHEBSSGMTm mod
              on bb.institutionbsbssgmtseqno=mod.institutionbsbssgmtseqno
          where 1=1 

		  	order by bb.rptdate desc,bb.INSTITUTIONBSBSSGMTSEQNO desc
		  	) A
	  	WHERE ROWNUM <=${endindex}
  	) where RN >${startindex}


select info.*
from PBCRS_REPORT_INSBALSHEBSSGMT info
where info.INSTITUTIONBSBSSGMTSEQNO= #{InstitutionBSSeqNo,jdbcType=VARCHAR}

mapper.update:
    update PBCRS_REPORT_INSBALSHEBSSGMT
    set ReportFlag= #{flagRes}
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}

		and REPORTFLAG = #{REPORTFLAG}

update PBCRS_REPORT_INSBALSHEBSSGMT
set RPTDATECODE= #{RptDateCode},
reportflag= #{reportFlag},
rptdate = #{rptDate}
where INSTITUTIONBSBSSGMTSEQNO = #{InstitutionBSBsSgmtSeqNo}

update PBCRS_REPORT_INSBALSHEBSSGMT
set IsDel= #{IsDel}
where INSTITUTIONBSBSSGMTSEQNO = #{InstitutionBSSeqNo}
