  #{RegCapCurrency,jdbcType=VARCHAR}, #{RegCap,jdbcType=VARCHAR}, #{MnShaHodInfoUpDate,jdbcType=VARCHAR}, #{ReportFlag,jdbcType=VARCHAR}
  )

mapper.select:

select MNSHAHODINFSGMTSEQNO, MNSHARHODNM, REGCAPCURRENCY, REGCAP, MNSHAHODINFOUPDATE,ReportFlag
from PBCRS_REPORT_MNSHAHODINFSGMT
where MNSHAHODINFSGMTSEQNO = #{MnShaHodInfSgmtSeqNo,jdbcType=VARCHAR}

    select count(1)
     from PBCRS_REPORT_EnBasInfShaHodInf ids
			    where ids.MnShaHodInfSgmtSeqNo=#{ENBASINFSEQNO}


    select * from (
	    SELECT A.*, ROWNUM RN  from (
			    select  ids.* 
     			from PBCRS_REPORT_EnBasInfShaHodInf ids 
			    where ids.MnShaHodInfSgmtSeqNo=#{ENBASINFSEQNO}


		  	) A
	  	WHERE ROWNUM <=${endindex}
  	) where RN >=${startindex}

mapper.insert:

    insert into PBCRS_REPORT_MNSHAHODINFSGMTM (MNSHAHODINFSGMTSEQNO, MNSHARHODNM, REGCAPCURRENCY, REGCAP, MNSHAHODINFOUPDATE,ReportFlag,etl_date

		) select MNSHAHODINFSGMTSEQNO, MNSHARHODNM, REGCAPCURRENCY, REGCAP, MNSHAHODINFOUPDATE,'0',to_date(to_char(sysdate,'yyyy-mm-dd'),'yyyy-mm-dd')
		from PBCRS_REPORT_MNSHAHODINFSGMT
    where MNSHAHODINFSGMTSEQNO = #{MnShaHodInfSgmtSeqNo,jdbcType=VARCHAR}

