mapper.update:

update PBCRS_REPORT_MOCIDCAGSINF
set INFRECTYPE = #{InfRecType,jdbcType=VARCHAR},
ODBNESCODE = #{OdBnesCode,jdbcType=VARCHAR},
NWBNESCODE = #{NwBnesCode,jdbcType=VARCHAR},
REPORTFLAG = #{reportflag,jdbcType=VARCHAR}
where MOCIDCAGSINFSEQNO = #{MoCIDCagsInfSeqNo,jdbcType=VARCHAR}

mapper.select:
select MOCIDCAGSINFSEQNO, INFRECTYPE, ODBNESCODE, NWBNESCODE,
REPORTFLAG
from PBCRS_REPORT_MOCIDCAGSINF
where reportflag='0'


	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}

mapper.update:
update PBCRS_REPORT_MOCIDCAGSINF
set REPORTFLAG='1'
where REPORTFLAG = '0'

	and sourcesys=#{sysid}



	and etl_date=#{etl_Date}
