mapper.insert:

insert into PBCRS_REPORT_MOTCLTCTRBSSGMT (MOTGACLTALCTRCTBSSGMTSEQNO, INFRECTYPE, 
  CCCODE, RPTDATE, RPTDATECODE, 
  INFOIDTYPE, NAME, CERTTYPE, 
  CERTNUM, MNGMTORGCODE, REPORTFLAG
  )
values (#{MotgaCltalCtrctBsSgmtSeqNo,jdbcType=VARCHAR}, #{InfRecType,jdbcType=VARCHAR}, 
  #{CcCode,jdbcType=VARCHAR}, #{RptDate,jdbcType=VARCHAR}, #{RptDateCode,jdbcType=VARCHAR}, 
  #{InfoIDType,jdbcType=VARCHAR}, #{Name,jdbcType=VARCHAR}, #{CertType,jdbcType=VARCHAR}, 
  #{CertNum,jdbcType=VARCHAR}, #{MngmtOrgCode,jdbcType=VARCHAR}, #{reportflag,jdbcType=VARCHAR}
  )

mapper.select:
	select * from (
     	 select info.* ,rownum RN from
      	(
              select MotgaCltalCtrctBsSgmtSeqNo,RptDate,InfoIDType,Name,CertType,CertNum,MngmtOrgCode,Sourcesys,ISDEL
       			from PBCRS_REPORT_MotCltCtrBsSgmt info1
                where 1=1

                 order by RptDate desc,MotgaCltalCtrctBsSgmtSeqNo desc
      	 )info where rownum <#{endindex}
  	) where RN >=#{startindex}

mapper.update:
    update PBCRS_REPORT_MOTCLTCTRBSSGMT
    set ReportFlag=#{flagRes}
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}


			and SOURCESYS IN('CRMS','SLNENT')


			and SOURCESYS IN('PLN','SLNIND','ILN')


			and MOTGACLTALCTRCTBSSGMTSEQNO in (${seqNoS})

		and REPORTFLAG = #{REPORTFLAG}

    update PBCRS_REPORT_MOTCLTCTRBSSGMTM
    set ReportFlag= '1'
    where 1=1

			and ETL_DATE = #{etl_Date}


			and SOURCESYS = #{sysid}


			and MOTGACLTALCTRCTBSSGMTSEQNO in (${seqNoS})

		and REPORTFLAG = #{REPORTFLAG}
