set PLENM = #{PleNm,jdbcType=VARCHAR},
  REPORTFLAG = #{reportflag,jdbcType=VARCHAR}
where MOTGAPROPTINFSGMTSEQNO = #{MotgaProptInfSgmtSeqNo,jdbcType=VARCHAR}

mapper.select:

select MOTGAPROPTINFSGMTSEQNO, PLENM, REPORTFLAG
from PBCRS_REPORT_MOTGAPROPTINFSGMT
where MOTGAPROPTINFSGMTSEQNO = #{MotgaProptInfSgmtSeqNo,jdbcType=VARCHAR}

  select * from (
   	 select info.* ,rownum RN from
    	(
              select MOTGAPROPTINFSGMTSEQNO, PLEINFSEQNO, PLETYPE, MOTGAPROPTIDTYPE, PLECERTID, PLEDISTR, 
       		 PLEVALUE, PLECY, VALORGTYPE, VALDATE, PLEDGORTYPE, PLEDGORNAME, PLEORCERTTYPE, PLEORCERTNUM, 
         		PLEDESC
        		from PBCRS_REPORT_MotgaProptInfSgmt info1
        		join PBCRS_REPORT_PleInf info2
        		on info1.MOTGAPROPTINFSGMTSEQNO=info2.COMRECINFSGMTSEQNO
         		 where info1.MOTGAPROPTINFSGMTSEQNO = #{ComRecInfSgmtSeqNo,jdbcType=VARCHAR}
    	 )info where rownum <#{endindex}
	) where RN >=#{startindex}

