mapper.delete:

delete from PBCRS_REPORT_OTRREC
where COMRECINFSGMTSEQNO = #{ComRecInfSgmtSeqNo,jdbcType=VARCHAR}
  and OTRRECSEQNO = #{OtrRecSeqNo,jdbcType=VARCHAR}

mapper.update:

update PBCRS_REPORT_OTRREC
set INFOIDTYPE = #{InfoIDType,jdbcType=VARCHAR},
  GUARNAME = #{GuarName,jdbcType=VARCHAR},
  GUARCERTTYPE = #{GuarCertType,jdbcType=VARCHAR},
  GUARCERTNUM = #{GuarCertNum,jdbcType=VARCHAR},
  REPORTFLAG = #{reportflag,jdbcType=VARCHAR}
where COMRECINFSGMTSEQNO = #{ComRecInfSgmtSeqNo,jdbcType=VARCHAR}
  and OTRRECSEQNO = #{OtrRecSeqNo,jdbcType=VARCHAR}

mapper.select:

select COMRECINFSGMTSEQNO, OTRRECSEQNO, INFOIDTYPE, GUARNAME, GUARCERTTYPE, GUARCERTNUM, 
REPORTFLAG
from PBCRS_REPORT_OTRREC
where COMRECINFSGMTSEQNO = #{ComRecInfSgmtSeqNo,jdbcType=VARCHAR}
  and OTRRECSEQNO = #{OtrRecSeqNo,jdbcType=VARCHAR}
